  delegate?: Maybe<Group>
  /** the id of the group responsible for risk oversight on behalf of the stakeholder */
  delegateID?: Maybe<Scalars['ID']['output']>
  /** details of the risk */
  details?: Maybe<Scalars['String']['output']>
